import React, { useState } from "react";
import { auth } from "../../firebase.js";
import { toast } from "react-toastify";
import { Button } from "antd";
import { useDispatch } from "react-redux";
import {googleAuthProvider} from "../../firebase"
import { MailOutlined } from "@ant-design/icons";
import firebase from "@firebase/app";
require('firebase/auth');






const Login = ({history}) => {
    const [email, setEmail] = useState("denishalimaj11@gmail.com");
    const [password, setPassword] = useState("denisdenis");
    const [loading, setLoading] = useState(false);
    let dispatch = useDispatch();

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        //console.table(email, password);


        try {
            const result = await auth.signInWithEmailandPassword(email, password);
           // console.log(result);
            const { user } = result;
            const idTokenResult = await user.getIdTokenResult();

            dispatch({
                type: "LOGGED_IN_USER",
                payload: {
                  email: user.email,
                  token: idTokenResult.token,

                },

            });
            history.push('/');

        } catch (error) {
            console.log(error);
            toast.error(error.message);
            setLoading(false);

        }


    };
    const googleLogin=async()=>{
        auth.signInWithPopUp(googleAuthProvider)
        .then(async(result) =>{
            const{user}=result
            const idTokenResult=await user.getIDTokenResult();
            dispatch({
                type: "LOGGED_IN_USER",
                payload: {
                  email: user.email,
                  token: idTokenResult.token,

                },

            });
            history.push('/');

        })
        .catch((err)=>{
            console.log(err);
            toast.error(error.message);


        });

    };
    const loginForm = () =>
        <form onSubmit={handleSubmit}>
            <div className="form-group">
                <input type="email" className="form-control" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="Your email" autoFocus />



            </div>

            <div className="form-group">
                <input type="password" className="form-control" value={password} onChange={(e) => setPassword(e.target.value)} placeholder="Your password" />


            </div>
            <br />
            <Button
                onClick={handleSubmit}
                type="primary"
                className="mb-3"
                block
                shape="round"
                size="large"
                disabled={!email || password.length < 6}>

            Login with Email/Password</Button>

        </form>
    return (
        <div className="container p-5">
            <div className="row">
                <div className="col-md-6 offset-md-3">
                    {loading ? (

                    <h4>Login</h4>):(<h4 className="text-danger">Loading...</h4>
                    )}
                    {loginForm()}
                    <Button
                onClick={googleLogin}
                type="danger"
                className="mb-3"
                block
                shape="round"
                size="large"
                >

            Login with Google</Button>

                </div>


            </div>

        </div>


    );



};
export default Login;
