import React, {useState, useEffect} from 'react'
import AdminNav from '../../../components/nav/AdminNav'
import {toast} from 'react-toastify'
import {useSelector} from 'react-redux'
import {getCategories} from "../../../functions/category"
import {createSub, getSub, removeSub} from "../../../functions/sub"
import  {Link} from "react-router-dom";
import {EditOutlined,DeleteOutlined} from "@ant-design/icons";
import CategoryForm from "../../../components/nav/forms/CategoryForm";
import LocalSearch from "../../../components/nav/forms/LocalSearch";

const SubUpdate =({match,history}) =>{
    const {user} = useSelector((state) => ({...state}));

    const [name, setName] = useState('');
    const [loading, setLoading] = useState(false);
    const [categories,setCategories]=useState([]);
    const [parent,setParent]=useState("");
    
    useEffect (()=>{
        loadCategories();
        loadSub();
    }, []);
    const loadCategories=() => getCategories().then((c)=>setCategories(c.data));
    const loadSub=() =>
     getSub(match.params.slug).then((s)=>{
        setName(s.data.name);
        setParent(s.data.parent);


    })

    const handleSubmit = (e) =>{
        e.preventDefault();
     //   console.log(name);
     setLoading(true);
     updateSub({name,parent}, user.token)    
    .then((res) => {
         //console.log(res)
         setLoading(false);
         setName("");
         toast.success(`"${res.data.name}" is created`);
         history.pushState('/admin/sub')
         
     })
     .catch((err) => {
         console.log(err);
         setLoading(false);
        if (err.response.status === 400) toast.error(err.response.data);
     // .catch(error => res.status(error.response.status).send(error.response.statusText);
     });
    };
    
    
   

    return (
        <div className="container-fluid">
        <div className="row">
            <div className="col-md-2">
                <AdminNav/></div>
            <div className="col">
                {loading ? ( <h4 className="text-danger">Loading..</h4>
                 ) : ( <h4>Update Subcategory</h4>
                 )}
                <div className="form-group">
                    <label>Parent Category</label>
                    <select
                    name="category" 
                    className="form-control"
                    onChange={e=>setCategory(e.target.value)}>
                        <option>Please select a category </option>
                        {categories.length > 0 &&
                         categories.map((c)=> (
                         <option key={c._id}value={c._id}>
                             {c.name}
                             </option>
                             
                             ))}
                    </select>
                </div>
                
                
                <CategoryForm handleSubmit={handleSubmit}
                name={name}
                setName={setName}/>
               

               

            
            </div>
        </div>

    </div>
    



    );



};
export default SubUpdate;
