import React, { useState,useEffect } from "react";
import { auth, googleAuthProvider } from "../../firebase.js";
import { toast } from "react-toastify";
import { Button } from "antd";
import { useDispatch,useSelector } from "react-redux";
import { GoogleOutlined, MailOutlined } from "@ant-design/icons";
import {Link} from 'react-router-dom'
import {axios} from 'axios';
import firebase from "firebase/app";
require('../../../node_modules/firebase/firebase-auth');

const createOrUpdateUser = async (authtoken) => {
    return await axios.post(
        `${process.env.REACT_APP_API}/create-or-update-user`,
         {},
          {
        headers: {
            authtoken,
        },
    }
    );
};




const Login = ({history}) => {
    const [email, setEmail] = useState("al47128@ubt-uni.net");
    const [password, setPassword] = useState("arberarber");
    const [loading, setLoading] = useState(false);
    const{user}=useSelector((state)=>({...state}));
    useEffect(() =>{

        if(user && user.token) history.push("/");


    }, [user]);
    let dispatch = useDispatch();

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        console.table(email, password);


        try {
            const result = await firebase.auth().signInWithEmailAndPassword(email, password);
           // console.log(result);
            const { user } = result;
            const idTokenResult = await user.getIdTokenResult();

            createOrUpdateUser(idTokenResult.token).then(res => console.log('CREATE OR UPDATE RES', res)).catch();

     //       dispatch({
       //         type: "LOGGED_IN_USER",
        //        payload: {
        //          email: user.email,
        //          token: idTokenResult.token,

        //        },

       //     });
       //     history.push('/');

        } catch (error) {
            console.log(error);
            toast.error(error.message);
            setLoading(false);

        }


    };
    const googleLogin = async () => {
        auth.signInWithPopup(googleAuthProvider)
            .then(async (result) => {
                const { user } = result
                const idTokenResult = await user.getIdTokenResult();

                dispatch({
                    type: "LOGGED_IN_USER",
                    payload: {
                        email: user.email,
                        token: idTokenResult.token,

                    },

                });
                history.push('/');

            })
            .catch((err) => {
                console.log(err)
                toast.error(err.message);

            });
    };



    const loginForm = () =>
        <form onSubmit={handleSubmit}>
            <div className="form-group">
                <input type="email" className="form-control" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="Your email" autoFocus />



            </div>

            <div className="form-group">
                <input type="password" className="form-control" value={password} onChange={(e) => setPassword(e.target.value)} placeholder="Your password" />


            </div>
            <br />
            <Button
                onClick={handleSubmit}
                type="primary"
                className="mb-3"
                block
                shape="round"
                size="large"
                disabled={!email || password.length < 6}>

            Login with Email/Password</Button>

        </form>
    return (
        <div className="container p-5">
            <div className="row">
                <div className="col-md-6 offset-md-3">
                    {loading ? (<h4 className="text-danger">Loading..</h4>) :
                       (<h4>Login</h4>) }

                    {loginForm()}

                    <Button
                onClick={googleLogin}
                type="danger"
                className="mb-3"
                block
                icon ={<GoogleOutlined/>}
                shape="round"
                size="large"
                >
                    Login with Google
                    </Button>
                <Link to="/forgot/password" className="float-right text-danger">Forgot Password</Link>






                </div>


            </div>

        </div>


    );



};
export default Login;
