import React, {useState, useEffect} from 'react'
import AdminNav from '../../../components/nav/AdminNav'
import {toast} from 'react-toastify'
import {useSelector} from 'react-redux'
import {getCategory,updateCategory} from "../../../functions/category"


const CategorUpdate =({history}) =>{
    const {user} = useSelector((state) => ({...state}));

    const [name, setName] = useState("");
    const [loading, setLoading] = useState(false);
    

    useEffect (()=>{
        loadCategories();



    }, [])
    const loadCategories=() => getCategories().then((c)=>setCategories(c.data));

    const handleSubmit = (e) =>{
        e.preventDefault();
     //   console.log(name);
     setLoading(true);
     createCategory({name}, user.token)    
    .then((res) => {
         //console.log(res)
         setLoading(false);
         setName("");
         toast.success(`"${res.data.name}" is created`);
         loadCategories();
     })
     .catch((err) => {
         console.log(err);
         setLoading(false);
        if (err.response.status === 400) toast.error(err.response.data);
     // .catch(error => res.status(error.response.status).send(error.response.statusText);
     });
    };
    const  handleRemove=async(slug)=>{
          //  let answer =window.confirm("Delete");
          //  console.log(answer,slug);
          if(window.confirm("Delete?")) {
              setLoading(true)
            removeCategory(slug,user.token)
            .then(res=>{
                setLoading(false);
                toast.error(`${res.data.name}deleted`);
                loadCategories();
                
                
    })
            .catch(err=>{
                if (err.response.status === 400){
                    setLoading(false);
                    toast.error(err.response.data);
                }


            });

          }


    };

const categoryForm = () => <form onSubmit={handleSubmit}>
    <div className="form-group">
        <label>Name</label>
        <input
         type="text"
         className="form-control" 
         onChange={e => setName(e.target.value)} 
         value={name}
         autoFocus
         required
          />
          <br/>
          <button className="btn btn-outline-primary">Save</button>
    </div>

    </form>;



    return (
        <div className="container-fluid">
        <div className="row">
            <div className="col-md-2">
                <AdminNav/></div>
            <div className="col">
                {loading ? ( <h4 className="text-danger">Loading..</h4>
                 ) : ( <h4>Create category</h4>
                 )}
                {categoryForm()}
                <hr/>
                {categories.map((c) =>(
                <div className="alert alert-secondary" key={c._id}>
                    
                    {c.name}<span onClick={()=>handleRemove(c.slug)}className="btn btn-sm float-right"><DeleteOutlined className="text-danger"/></span><Link to ={`/admin/category/${c.slug}`}><span className="btn btn-sm float-right"><EditOutlined className="text-warning"/></span></Link>
                    </div>
                ))}
            </div>
        </div>

    </div>
    



    );



};
export default CategoryUpdate;
